import React, { useEffect, useState } from "react";
import axios from "axios";

export default function FeaturesSection() {
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Replace {id} with the ID of the blog you want to fetch
  const blogId = 1; // Example blog ID
  const apiUrl = `https://tribepub.storeredesign.com/wp-json/wp/v2/posts/{70}`;

  useEffect(() => {
    axios
      .get(apiUrl)
      .then((response) => {
        setBlog(response.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching blog:", err);
        setError("Could not fetch blog data.");
        setLoading(false);
      });
  }, [apiUrl]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <section className="py-16">
      <div className="max-w-[1170px] mx-auto px-4 space-y-32">
        {/* Blog Feature */}
        <div className="grid md:grid-cols-2 gap-12 items-center">
          {/* Blog Image */}
          {blog.featured_media && (
            <img
              src={blog.featured_media_url} // Replace with the correct image URL field
              alt={blog.title.rendered}
              width={600}
              height={400}
              className="rounded-lg"
            />
          )}

          {/* Blog Content */}
          <div>
            <h2
              className="text-3xl md:text-4xl font-bold mb-4"
              dangerouslySetInnerHTML={{ __html: blog.title.rendered }}
            ></h2>
            <div
              className="text-gray-600 mb-6"
              dangerouslySetInnerHTML={{ __html: blog.content.rendered }}
            ></div>
            <button className="bg-amber-500 hover:bg-amber-600 px-8 py-4 text-white text-lg button-bg rounded-full">
              Read More
            </button>
          </div>
        </div>
      </div>
    </section>
  );
}
