import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Facebook, Youtube, Instagram, Twitter } from 'lucide-react';
import Tribepub from "../Assets/image 1.png";

export default function Footer() {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('https://tribepub.storeredesign.com/wp-json/wp-api-menus/v2/menus/footer')
      .then((response) => response.json())
      .then((data) => {
        setMenuItems(data.items || []);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching footer menu:', error);
        setMenuItems([]);
        setLoading(false);
      });
  }, []);

  return (
    <footer className="bg-black text-white">
      <div className="max-w-[1170px] mx-auto px-4 py-12">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 border-b border-gray-800 pb-12">
          <div className="space-y-6">
            <img src={Tribepub} alt="TribePUB" width={200} height={50} className="mb-6" />
            <p className="text-gray-400">
              We architect and build a Platform Unified Business ("PUB") for you the Tribe Leader and your Tribe.
            </p>
            <div className="flex space-x-4">
              <Link to="#" className="text-amber-500 hover:text-amber-400"><Facebook className="w-6 h-6" /></Link>
              <Link to="#" className="text-amber-500 hover:text-amber-400"><Youtube className="w-6 h-6" /></Link>
              <Link to="#" className="text-amber-500 hover:text-amber-400"><Instagram className="w-6 h-6" /></Link>
              <Link to="#" className="text-amber-500 hover:text-amber-400"><Twitter className="w-6 h-6" /></Link>
            </div>
          </div>

          <div>
            <h3 className="text-amber-500 font-semibold text-lg mb-4">Quick Links</h3>
            <nav aria-label="Footer Menu">
              <ul className="space-y-2">
                {loading ? (
                  <li>
                    <div className="flex items-center">
                      <svg
                        className="animate-spin w-5 h-5 mr-2 text-amber-500"
                        xmlns="http://www.w3.org/2000/svg"
                        fill="none"
                        viewBox="0 0 24 24"
                      >
                        <circle
                          className="opacity-25"
                          cx="12"
                          cy="12"
                          r="10"
                          stroke="currentColor"
                          strokeWidth="4"
                        ></circle>
                        <path
                          className="opacity-75"
                          fill="currentColor"
                          d="M4 12a8 8 0 018-8v4a4 4 0 00-4 4H4z"
                        ></path>
                      </svg>
                      Loading menu...
                    </div>
                  </li>
                ) : menuItems.length > 0 ? (
                  menuItems.map((item) => (
                    <li key={item.id}>
                      <a href={item.url} className="text-gray-400 hover:text-white">
                        {item.title}
                      </a>
                    </li>
                  ))
                ) : (
                  <li>No menu items found</li>
                )}
              </ul>
            </nav>
          </div>
          {/* Add Community and About Sections Here */}
        </div>
      </div>
    </footer>
  );
}
