import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Tribepub from "../Assets/image 1.png";
import { Facebook, Youtube, Instagram, Twitter } from 'lucide-react';

export default function Footer() {
  const currentYear = new Date().getFullYear();
  const [footerMenuItems, setFooterMenuItems] = useState([]);

  useEffect(() => {
    // Fetch the "Footer" Menu from WordPress via REST API
    fetch("https://tribepub.storeredesign.com/wp-json/wp-api-menus/v2/menus/footer")
      .then(response => response.json())
      .then(data => {
        console.log("Fetched footer menu data:", data); // Log the response
        setFooterMenuItems(data.items || []);
      })
      .catch(error => {
        console.error('Error fetching footer menu data:', error);
        setFooterMenuItems([]); // Fallback to empty array in case of error
      });
  }, []);

  return (
    <footer className="bg-black text-white">
      <div className="max-w-[1170px] mx-auto px-4 py-12">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 border-b border-gray-800 pb-12">
          {/* Logo and Description Section */}
          <div className="space-y-6">
            <img
              src={Tribepub}
              alt="TribePUB"
              width={200}
              height={50}
              className="mb-6"
            />
            <p className="text-gray-400">
              We architect and build a Platform Unified Business ("PUB") for you the Tribe Leader and your Tribe.
            </p>
            <div className="flex space-x-4">
              <Link to="#" className="text-amber-500 hover:text-amber-400">
                <Facebook className="w-6 h-6" />
              </Link>
              <Link to="#" className="text-amber-500 hover:text-amber-400">
                <Youtube className="w-6 h-6" />
              </Link>
              <Link to="#" className="text-amber-500 hover:text-amber-400">
                <Instagram className="w-6 h-6" />
              </Link>
              <Link to="#" className="text-amber-500 hover:text-amber-400">
                <Twitter className="w-6 h-6" />
              </Link>
            </div>
          </div>

          {/* Footer Menu Section */}
          <div>
            <h3 className="text-amber-500 font-semibold text-lg mb-4">Footer</h3>
            <ul className="space-y-2">
              {footerMenuItems.length > 0 ? (
                footerMenuItems.map(item => (
                  <li key={item.ID}>
                    <Link
                      to={item.url}
                      className="text-gray-400 hover:text-white"
                    >
                      {item.title}
                    </Link>
                  </li>
                ))
              ) : (
                <li>Loading footer menu...</li> // Show loading state if menu is still being fetched
              )}
            </ul>
          </div>

          {/* Other Sections */}
          {/* Add other sections like Community, About, etc. */}
        </div>

        {/* Copyright Section */}
        <div className="pt-8 space-y-4 text-sm text-gray-400">
          <p className="text-center">Â©{currentYear} - TribePub All Rights Reserved.</p>
        </div>
      </div>
    </footer>
  );
}
